// import Layout from '../components/MyLayout.js'
import Header from '../components/Header.js'
import Link from 'next/link'
import { Component } from "react";
import { spring } from 'popmotion';

class HomePage extends Component {

  constructor(props) {
      super(props);
      this.state = {
        counter: 0
      }
  }

  increaseCounter(type){
    this.setState(prevState => {
       return {count: type == 'increase' ? prevState.count + 1: prevState.count - 1}
    });
    localStorage.setItem("counter",count);
  }

  render() {
    return (
      <main>
        <Header />

        <h1 style={{ fontFamily:"Arial", fontSize:"50px" }}>Magic 8 Ball</h1>

        <form className="question-input" method="POST" action="/">
          <input type="text" ref="inputquestion" placeholder="Ask your question..."style={{ width: "300px", fontSize:18 }}/>
          <button onClick={this.increaseCounter.bind(this, 'increase')} id="submitquestion" style={{ width: "100px", fontSize:17 }}>Shake Me!</button>
        </form>

        <p>Count: {this.state.counter}</p>

      </main>
    )
  }
}

export default HomePage;
