// import Layout from '../components/MyLayout.js'
import Header from '../components/Header.js'
import Link from 'next/link'
import React, { Component } from "react";
import { spring } from 'popmotion';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { Circle } from 'react-shapes';
const moment = require('moment');
import LinearProgress from '@material-ui/core/LinearProgress';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';

const styles = {
    textField: {
    fontSize: 500,
    fontColor:'white' //works!
 }
}

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      response: null
    };
    this.incrementCounter = this.incrementCounter.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    this.setState({
      count: parseInt(localStorage.getItem("count")) || 0
    });
  }

  incrementCounter() {
    const count = this.state.count + 1;
    localStorage.setItem("count", count);
    this.setState({
      count: count
    });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    fetch('/', { method: 'POST' })
    .then(response => response.text())
    .then(textValue => this.setState({ response: textValue }))
    .then(time => this.setState({ time: Date() }))
    .then(console.log(moment().toDate()));
  }

  render() {
    return (
        <main>
            <Header />
              <div style={{marginTop:"100px", display:'flex', marginLeft:"500px", justifyContent:'center', alignItems:'center'}} id="circle">
                <Typography variant="title" color="inherit">
                  <div style={{display: 'flex',  justifyContent:'center', alignItems:'center'}}>
                    <h1 style={{ fontFamily:"Arial", fontSize:"50px", color:"#5a79aa"}}>Magic 8 Ball</h1>
                  </div>
                  <div style={{display: 'flex',  justifyContent:'center', alignItems:'center'}}>
                    <form className="question-input" onSubmit={this.handleSubmit}>
                      <TextField
                        id="inputquestion"
                        autoComplete="off"
                        placeholder="Ask your question..."
                        InputProps={{
                          style: {
                            color:'white'
                          }
                        }}
                        InputLabelProps={{
                          style: {
                            color:'white'
                          }
                        }}
                        margin="normal"
                        style={{ width:"200px", paddingRight:"10px", color:'white'}}
                      />
                      <Button
                        variant="contained"
                        type="submit"
                        color="primary"
                        onClick={ this.incrementCounter.bind(this) }
                        id="submitquestion"
                        style={{ width: "100px", fontSize:17, background:"#bc2727" }}
                        id="input">
                        Shake Me!
                      </Button>
                    </form>
                  </div>
                  <div style={{display: 'flex',  justifyContent:'center', alignItems:'center', color:"white", fontWeight:'bold'}}>
                    <p>{this.state.response}</p>
                  </div>
                </Typography>
              </div>
              <style jsx>{`
                #circle {
                	width: 500px;
                	height: 500px;
                	background: #191b1e;
                	border-radius: 1000px;
                  -webkit-box-shadow:0 0 90px grey;
                  -moz-box-shadow: 0 0 90px grey;
                  box-shadow:0 0 90px grey;
                },
                input: {
                  color: 'white'
                }`}
              </style>
        </main>
    )
  }
}

export default App;
